package com.bloxbean.cardano.client.backend.api;

import com.bloxbean.cardano.client.api.exception.ApiException;
import com.bloxbean.cardano.client.api.model.Amount;
import com.bloxbean.cardano.client.api.model.Result;
import com.bloxbean.cardano.client.api.model.Utxo;
import com.bloxbean.cardano.client.backend.blockfrost.service.BFTransactionService;
import com.bloxbean.cardano.client.api.model.EvaluationResult;
import com.bloxbean.cardano.client.backend.model.TransactionContent;
import com.bloxbean.cardano.client.backend.model.TxContentRedeemers;
import com.bloxbean.cardano.client.backend.model.TxContentUtxo;
import com.bloxbean.cardano.client.util.HexUtil;
import com.bloxbean.cardano.client.util.JsonUtil;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import java.util.List;

import static com.bloxbean.cardano.client.common.ADAConversionUtil.adaToLovelace;
import static com.bloxbean.cardano.client.common.CardanoConstants.LOVELACE;
import static org.assertj.core.api.Assertions.assertThat;
import static org.junit.jupiter.api.Assertions.*;

class TransactionServiceIT extends BaseITTest {

    TransactionService service;

    @BeforeEach
    public void setup() {
        service = getBackendService().getTransactionService();
    }

    @Test
    void testSubmitInvalidTransaction() throws ApiException {

        Result<String> result = service.submitTransaction(new byte[0]);

        System.out.println(result);
        assertFalse(result.isSuccessful());
        assertEquals(400, result.code());
    }

    @Test
    void testGetTransaction() throws Exception {
        String txnHash = "fb77161efb9788f78d13e815e7c0bfde192cbdfb18a8e502c7a3d3171c1e5688";
        Result<TransactionContent> result = service.getTransaction(txnHash);

        assertNotNull(result.getValue());
        System.out.println(JsonUtil.getPrettyJson(result.getValue()));
    }

    @Test
    void testGetTransactions() throws Exception {
        String txnHash = "fb77161efb9788f78d13e815e7c0bfde192cbdfb18a8e502c7a3d3171c1e5688";
        Result<List<TransactionContent>> result = service.getTransactions(List.of(txnHash));

        assertNotNull(result.getValue());
        assertFalse(result.getValue().isEmpty());
        assertEquals(txnHash, result.getValue().get(0).getHash());
        System.out.println(JsonUtil.getPrettyJson(result.getValue()));
    }

    @Test
    void testInvalidTransactionsFormat() {
        List<String> txList = List.of("test", "83b9df2741b964ecd96e44f062e65fad451d22e2ac6ce70a58c56339feda525e");
        assertThrows(ApiException.class, () -> service.getTransactions(txList));
    }

    @Test
    void testTransactionsNotFound() throws ApiException {
        List<String> txList = List.of("83b9");
        Result<List<TransactionContent>> result = service.getTransactions(txList);

        assertFalse(result.isSuccessful());
        assertEquals(404, result.code());
    }

    @Test
    void testGetTransactionUtxos() throws Exception {
        String txnHash = "ac2f821fda7b2488e9f9da05b9013134cfe2958ed210466d44e66136f1b3ca94";
        Result<TxContentUtxo> result = service.getTransactionUtxos(txnHash);

        assertNotNull(result.getValue());
        System.out.println(JsonUtil.getPrettyJson(result.getValue()));
    }

    @Test
    void testGetTransactionReedemers() throws Exception {
        String txnHash = "0bbfc8e75965aebcaf3fbe31147f1615f03c7e165c1fa3e2c7caeac0167e3821";
        Result<List<TxContentRedeemers>> result = service.getTransactionRedeemers(txnHash);

        assertNotNull(result.getValue());
        System.out.println(JsonUtil.getPrettyJson(result.getValue()));
    }

    @Test
    void evaluateTx_withEvaluationFailure() throws ApiException {
        if (!(service instanceof BFTransactionService))
            return;

        String txHex = "";

        byte[] cborBytes = HexUtil.decodeHexString(txHex);
        Result<List<EvaluationResult>> evaluationResults = service.evaluateTx(cborBytes);

        assertThat(evaluationResults.isSuccessful()).isFalse();
        assertThat(evaluationResults.getValue()).isEmpty();
    }

    @Test
    void evaluateTx_withFault() throws ApiException {
        if (!(service instanceof BFTransactionService))
            return;

        String txHex = "";

        byte[] cborBytes = HexUtil.decodeHexString(txHex);
        Result<List<EvaluationResult>> evaluationResults = service.evaluateTx(cborBytes);

        assertThat(evaluationResults.isSuccessful()).isFalse();
        assertThat(evaluationResults.getValue()).isEmpty();
    }

    @Test
    public void getOutput_whenOutput_exists_with_datum() throws ApiException {
        String txHash = "33383bfecb9e541d32857eda88b18ffc71943372fe8ae7b4792589b72a41e26e";
        int outputIndex = 2;

        Result<Utxo> result = service.getTransactionOutput(txHash, outputIndex);

        assertTrue(result.isSuccessful());
        assertTrue(result.code() == 200);
        assertEquals("addr_test1wpu365zw7petuyvhng78y2l3534g4ammuuexldu5nkjf0sgz5xu88", result.getValue().getAddress());
        if (!backendType.equals(KOIOS)) { //TODO -- Remove after the fix in koios-backend
            assertEquals("4194bb3c4c0fd47485112d09ea85b2dd6ab44fa826b77cbf9ed0f12582b057d9", result.getValue().getDataHash());
            assertEquals("d87a9fd8799fd8799f581cd1707e481671d473ee5a8d561aaac4a1f4e8c937ce61e5d11fc0611fffd8799fd8799f1a000687241b00000187a8d155a2ffffffff", result.getValue().getInlineDatum());
        }
        assertEquals(3, result.getValue().getAmount().size());
        assertEquals(new Amount(LOVELACE, adaToLovelace(2)), result.getValue().getAmount().get(0));
    }

    @Test
    public void getOutput_whenOutput_exists_with_referenceInput() throws ApiException {
        String txHash = "d8109586a0dfb1bdc62bec0e6b41f3825994380f32ecad609792587ed3080d10";
        int outputIndex = 0;

        Result<Utxo> result = service.getTransactionOutput(txHash, outputIndex);

        assertTrue(result.isSuccessful());
        assertTrue(result.code() == 200);
        assertEquals("addr_test1wzcppsyg36f65jydjsd6fqu3xm7whxu6nmp3pftn9xfgd4ckah4da", result.getValue().getAddress());
        if (!backendType.equals(KOIOS)) { //TODO -- Remove after the fix in koios-backend
            assertEquals("b010c0888e93aa488d941ba4839136fceb9b9a9ec310a573299286d7", result.getValue().getReferenceScriptHash());
        }
        assertEquals(1, result.getValue().getAmount().size());
        assertEquals(new Amount(LOVELACE, adaToLovelace(9.34408)), result.getValue().getAmount().get(0));
    }

    @Test
    public void getOutput_whenOutput_invalidIndex() throws ApiException {
        String txHash = "d8109586a0dfb1bdc62bec0e6b41f3825994380f32ecad609792587ed3080d10";
        int outputIndex = 5;

        Result<Utxo> result = service.getTransactionOutput(txHash, outputIndex);

        assertFalse(result.isSuccessful());
        assertEquals(404, result.code());
    }

    @Test
    public void getOutput_whenOutput_invalidTxHash() throws ApiException {
        String txHash = "e8109586a0dfb1bdc62bec0e6b41f3825994380f32ecad609792587ed3080d10";
        int outputIndex = 5;

        Result<Utxo> result = service.getTransactionOutput(txHash, outputIndex);

        assertFalse(result.isSuccessful());
        assertEquals(404, result.code());
    }

}
