package com.bloxbean.cardano.client.supplier.ogmios;

import com.bloxbean.cardano.client.account.Account;
import com.bloxbean.cardano.client.address.AddressProvider;
import com.bloxbean.cardano.client.api.exception.ApiException;
import com.bloxbean.cardano.client.api.model.Amount;
import com.bloxbean.cardano.client.api.model.EvaluationResult;
import com.bloxbean.cardano.client.api.model.Result;
import com.bloxbean.cardano.client.api.model.Utxo;
import com.bloxbean.cardano.client.common.model.Networks;
import com.bloxbean.cardano.client.function.helper.ScriptUtxoFinders;
import com.bloxbean.cardano.client.function.helper.SignerProviders;
import com.bloxbean.cardano.client.plutus.spec.BigIntPlutusData;
import com.bloxbean.cardano.client.plutus.spec.PlutusData;
import com.bloxbean.cardano.client.plutus.spec.PlutusV2Script;
import com.bloxbean.cardano.client.quicktx.QuickTxBuilder;
import com.bloxbean.cardano.client.quicktx.ScriptTx;
import com.bloxbean.cardano.client.quicktx.Tx;
import com.bloxbean.cardano.client.util.HexUtil;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import java.math.BigInteger;
import java.util.List;
import java.util.Optional;
import java.util.Random;

import static org.assertj.core.api.Assertions.assertThat;
import static org.junit.jupiter.api.Assertions.assertTrue;

public class OgmiosTransactionProcessorIT extends OgmiosBaseTest {
    QuickTxBuilder quickTxBuilder;
    String sender1Addr;
    Account sender1;
    String receiver1 = "addr_test1qz3s0c370u8zzqn302nppuxl840gm6qdmjwqnxmqxme657ze964mar2m3r5jjv4qrsf62yduqns0tsw0hvzwar07qasqeamp0c";
    String receiver2 = "addr_test1qqwpl7h3g84mhr36wpetk904p7fchx2vst0z696lxk8ujsjyruqwmlsm344gfux3nsj6njyzj3ppvrqtt36cp9xyydzqzumz82";

    @BeforeEach
    public void setup() {
        quickTxBuilder = new QuickTxBuilder(kupoUtxoSupplier, ogmiosProtocolParamSupplier, ogmiosTransactionProcessor);
        String senderMnemonic = "drive useless envelope shine range ability time copper alarm museum near flee wrist live type device meadow allow churn purity wisdom praise drop code";
        sender1 = new Account(Networks.testnet(), senderMnemonic);
        sender1Addr = sender1.baseAddress();
    }

    @Test
    void evaluateTx_cbor() throws ApiException {
        String txHex = "";
        byte[] cbor = HexUtil.decodeHexString(txHex);

        Result<List<EvaluationResult>> evaluationResult = ogmiosTransactionProcessor.evaluateTx(cbor);
        assertThat(evaluationResult.isSuccessful()).isEqualTo(true);
        assertThat(evaluationResult.getValue().get(0).getExUnits().getMem().longValue()).isGreaterThan(10);
        assertThat(evaluationResult.getValue().get(0).getExUnits().getSteps().longValue()).isGreaterThan(10);
        assertThat(evaluationResult.getValue().get(0).getRedeemerTag()).isNotNull();
    }

    @Test
    void evaluateTx_cbor_error() throws ApiException {
        String txHex = "";
        byte[] cbor = HexUtil.decodeHexString(txHex);

        Result<List<EvaluationResult>> evaluationResult = ogmiosTransactionProcessor.evaluateTx(cbor);
        System.out.println(evaluationResult);
        assertThat(evaluationResult.isSuccessful()).isEqualTo(false);
        assertThat(evaluationResult.getResponse()).isNotNull();
    }

    @Test
    void submitTx() {
        Tx tx = new Tx()
                .payToAddress(receiver2, Amount.ada(2))
                .from(sender1Addr);

        Result<String> result = quickTxBuilder.compose(tx)
                .withSigner(SignerProviders.signerFrom(sender1))
                .complete();

        System.out.println(result);
        assertTrue(result.isSuccessful());
        checkIfUtxoAvailable(result.getResponse(), sender1Addr);
    }

    @Test
    void alwaysTrueScript_guessSumScript() throws ApiException {
        PlutusV2Script alwaysTrueScript = PlutusV2Script.builder()
                .type("PlutusScriptV2")
                .cborHex("49480100002221200101")
                .build();

        String alwaysTrueScriptAddr = AddressProvider.getEntAddress(alwaysTrueScript, Networks.testnet()).toBech32();
        Amount alwaysTrueScriptAmt = Amount.lovelace(BigInteger.valueOf(2479280));

        Random rand = new Random();
        int randInt = rand.nextInt();
        BigIntPlutusData alwaysTruePlutusData =  new BigIntPlutusData(BigInteger.valueOf(randInt)); //any random number

        //Sum Script
        PlutusV2Script sumScript =
                PlutusV2Script.builder()
                        .cborHex("")
                        .build();
        String sumScriptAddr = AddressProvider.getEntAddress(sumScript, Networks.testnet()).toBech32();
        Amount sumScriptAmt = Amount.ada(4.0);
        PlutusData sumScriptDatum =  new BigIntPlutusData(BigInteger.valueOf(8)); //redeemer should be 36
        PlutusData sumScriptRedeemer = new BigIntPlutusData(BigInteger.valueOf(36));

        Tx tx = new Tx();
        tx.payToContract(alwaysTrueScriptAddr, alwaysTrueScriptAmt, alwaysTruePlutusData)
                .payToContract(sumScriptAddr, sumScriptAmt, sumScriptDatum)
                .from(sender1Addr);

        QuickTxBuilder quickTxBuilder = new QuickTxBuilder(kupoUtxoSupplier, ogmiosProtocolParamSupplier, ogmiosTransactionProcessor);
        Result<String> result = quickTxBuilder.compose(tx)
                .withSigner(SignerProviders.signerFrom(sender1))
                .completeAndWait(System.out::println);

        System.out.println(result);
        if (result.isSuccessful())
            checkIfUtxoAvailable(result.getValue(), alwaysTrueScriptAddr);

        Optional<Utxo> alwaysTrueUtxo  = ScriptUtxoFinders.findFirstByInlineDatum(kupoUtxoSupplier, alwaysTrueScriptAddr, alwaysTruePlutusData);
        Optional<Utxo> sumUtxo  = ScriptUtxoFinders.findFirstByInlineDatum(kupoUtxoSupplier, sumScriptAddr, sumScriptDatum);
        ScriptTx scriptTx = new ScriptTx()
                .collectFrom(alwaysTrueUtxo.get(), PlutusData.unit())
                .collectFrom(sumUtxo.get(), sumScriptRedeemer)
                .payToAddress(receiver1, List.of(alwaysTrueScriptAmt, sumScriptAmt))
                .attachSpendingValidator(alwaysTrueScript)
                .attachSpendingValidator(sumScript)
                .withChangeAddress(sumScriptAddr, sumScriptDatum);

        Result<String> result1 = quickTxBuilder.compose(scriptTx)
                .feePayer(sender1Addr)
                .withSigner(SignerProviders.signerFrom(sender1))
                .withTxInspector(transaction -> {
                    System.out.println("Tx: " + transaction);
                }).completeAndWait(System.out::println);

        System.out.println(result1.getResponse());
        assertTrue(result1.isSuccessful());

        checkIfUtxoAvailable(result1.getValue(), sender1Addr);
    }

    protected void checkIfUtxoAvailable(String txHash, String address) {
        Optional<Utxo> utxo = Optional.empty();
        int count = 0;
        while (utxo.isEmpty()) {
            if (count++ >= 20)
                break;
            List<Utxo> utxos = kupoUtxoSupplier.getAll(address);
            utxo = utxos.stream().filter(u -> u.getTxHash().equals(txHash))
                    .findFirst();
            System.out.println("Try to get new output... txhash: " + txHash);
            try {
                Thread.sleep(1000);
            } catch (Exception e) {}
        }
    }
}
